package baekjoon_for;
import java.util.Scanner;

public class Nout {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner = new Scanner(System.in);
		
		int N = scanner.nextInt(); 

		
		for(int i =1; i<= N ; i++) {
			System.out.println(i);
		}
		
	}

}
